Page._content:
/* 默认情况下缩进左侧66px，只留一点箭头部分 */
.aplayer.aplayer-fixed.aplayer-narrow .aplayer-body {
  left: -66px !important;
}
/* 鼠标点击时左侧缩进归零，完全显示按钮 */
.aplayer.aplayer-fixed.aplayer-narrow .aplayer-body:click {
  left: 0 !important;
}

/* 颜色配置 */
/*歌单背景 */
[data-theme="dark"]
  .aplayer{
    background: rgba(43,43,43,0.85);
  }
/* 鼠标选取背景 */
[data-theme="dark"]
  .aplayer-list li:hover{
    background: rgba(55,55,55,0.85) !important;
  }
/* 正在播放的歌曲的背景 */
[data-theme="dark"]
  .aplayer-list-light{
    background:rgba(63,63,63,0.9) !important;
  }
/* 下方主体 */
[data-theme="dark"]
  .aplayer-body{
    background:rgba(33,33,33,0.85) !important;
  }
/* 伸缩按钮 */
