    int nums[n];
    for (int i = 0; i < n; ++i) scanf("%d", &nums[i]);
    for (int i = 0; i < n; ++i) {
        task(nums, i, n);
    }
    for (int i = 0; i < n; ++i) printf(" %d", nums[i]);
