    scanf("%s", s1);
    scanf("%s", s2);
    //判断两个字符串是否相等
    if (strcmp(s1,s2) == 0) printf("Equal");
    else {
        int s1_num[10] = {0}, s2_num[10] = {0};
        //统计各个数字的出现次数
        for (int i = 0; i < strlen(s1); i++) s1_num[s1[i] - '0']++;
        for (int i = 0; i < strlen(s2); i++) s2_num[s2[i] - '0']++;
